local SCRIPT_NAME = "Loader"local MAJORVERSION = 1local SUBVERSION = 08051543-------------------------------------------------------------------------------------------------------------local versionmessage =	" "local UPDATE_CHANGE_LOG = " "-------------------------------------------------------------------------------------------------------------local AUTOUPDATE = truelocal Locale = GetLocalization() --ko_KRlocal VERSION = tostring(MAJORVERSION) .. "." .. tostring(SUBVERSION)local Client	= os.getenv('APPDATA')local UPDATE_TEMP_FILE 	= BOL_PATH.."Scripts\\loaderDumpas"local PATH 							= BOL_PATH.."Scripts\\NEKO-Loader.lua"local URL	= "https://raw.githubusercontent.com/livewa/Core/master/NEKO-Loader.lua"--{function Update()	file = io.open(UPDATE_TEMP_FILE, "rb")	if file ~= nil then		content = file:read("*all")		file:close()		os.remove(UPDATE_TEMP_FILE)				if content then					local update_MAJORVERSION = string.match(string.match(content, "local MAJORVERSION = %d+"), "%d+")			local update_SUBVERSION = string.match(string.match(content, "local SUBVERSION = %d+"), "%d+")			local update_VERSION = tostring(update_MAJORVERSION) .. "." .. tostring(update_SUBVERSION)			update_MAJORVERSION = tonumber(string.format("%d", update_MAJORVERSION))			update_SUBVERSION = tonumber(string.format("%d", update_SUBVERSION))			if (update_MAJORVERSION and update_SUBVERSION) and (update_MAJORVERSION > MAJORVERSION or (update_MAJORVERSION == MAJORVERSION and update_SUBVERSION > SUBVERSION)) and content:find("NEKO") then				file = io.open(PATH, "w")				if file ~= nil then					file:write(content)					file:flush()					file:close()					if Locale == "ko_KR" then						PrintChat("<font color=\"#81BEF7\">" .. SCRIPT_NAME .. ": </font> <font color=\"#00FF00\">v" .. update_VERSION .. "로 업데이트 되었습니다. 리로드 해 주세요. v</font>")						PrintChat("<font color=\"#81BEF7\">" .. SCRIPT_NAME .. ": </font> <font color=\"#00FF00\">업데이트 메세지: " .. UPDATE_CHANGE_LOG .. "</font>")					else						PrintChat("<font color=\"#81BEF7\">" .. SCRIPT_NAME .. ": </font> <font color=\"#00FF00\">v" .. update_VERSION .. " updated. reload pleas,</font>")						PrintChat("<font color=\"#81BEF7\">" .. SCRIPT_NAME .. ": </font> <font color=\"#00FF00\">Update message: " .. UPDATE_CHANGE_LOG .. "</font>")										end				else					if Locale == "ko_KR" then						PrintChat("<font color=\"#81BEF7\">" .. SCRIPT_NAME .. ": </font> <font color=\"#FF0000\">v" .. update_VERSION .. "로 업데이트 실패.</font>")					else						PrintChat("<font color=\"#81BEF7\">" .. SCRIPT_NAME .. ": </font> <font color=\"#FF0000\">v" .. update_VERSION .. " update failed.</font>")					end				end			elseif (update_MAJORVERSION and update_SUBVERSION) and (update_MAJORVERSION == MAJORVERSION and update_SUBVERSION == SUBVERSION) then				if Locale == "ko_KR" then					PrintChat("<font color=\"#81BEF7\">" .. SCRIPT_NAME .. ": </font> <font color=\"#00FF00\">업데이트 사항이 없습니다.</font>")				else					PrintChat("<font color=\"#81BEF7\">" .. SCRIPT_NAME .. ": </font> <font color=\"#00FF00\">No updates. It's lastest version</font>")				end			elseif (update_MAJORVERSION  and update_SUBVERSION) then				if Locale == "ko_KR" then					PrintChat("<font color=\"#81BEF7\">" .. SCRIPT_NAME .. ": </font> <font color=\"#00FF00\">최신 버전이 이미 인스톨 되어 있습니다. v"..update_VERSION.."이 있지만 다운 받지 않습니다.</font>")				else					PrintChat("<font color=\"#81BEF7\">" .. SCRIPT_NAME .. ": </font> <font color=\"#00FF00\">v"..update_VERSION.." is exist, but already installed new version.</font>")				end			end		end	endendfunction OnCreateObj(object)endfunction OnDeleteObj(object)endfunction OnTick()	if not neko then if not Init() then return end endendfunction OnLoad()		if AUTOUPDATE then DownloadFile(URL, UPDATE_TEMP_FILE, Update) endendclass '_Neko_AI'function Init()	function _Neko_AI:__init()		require "NEKO-AI"		if neko_OnLoad          then neko_OnLoad() end		if neko_OnTick          then AddTickCallback(function() neko_OnTick() end) end		if neko_OnCreateObj     then AddCreateObjCallback(function(object) neko_OnCreateObj(object) end) end		if neko_OnDeleteObj     then AddDeleteObjCallback(function(object) neko_OnDeleteObj(object) end) end		if neko_OnProcessSpell  then AddProcessSpellCallback(function(unit, spell) neko_OnProcessSpell(unit, spell) end) end		if neko_OnAnimation     then AddAnimationCallback(function(unit, animation) neko_OnAnimation(unit, animation) end) end		if neko_OnApplyParticle then AddParticleCallback(function(Unit, particle) neko_OnApplyParticle(Unit, particle) end) end		if neko_OnDraw          then AddDrawCallback(function() neko_OnDraw() end) end		if neko_OnSendChat      then AddChatCallback(function(text) neko_OnSendChat(text) end) end				if neko_OnWndMsg        then AddMsgCallback(function(msg, keycode) neko_OnWndMsg(msg, keycode) end) end	end	neko = true	_Neko_AI()end--}